# @generic head start
MOUNTPOINT=$1
INFO="$2 $0"
ARCH=$(/bin/uname -m)
HOSTNAME=$(/bin/hostname -s)

# info
logger "$INFO: start"
logger "$INFO: copying firmware, software and libraries to ramdisk"

# @generic head end


# specific 
# software
cp -a $MOUNTPOINT/$ARCH/usr/bin/dmunipz-ctl /usr/bin/
# firmware
cp -a $MOUNTPOINT/firmware/* /


# @tail
# info
logger "$INFO: done"
