library ieee;
use ieee.std_logic_1164.all;
use ieee.numeric_std.all;
use work.wishbone_pkg.all;
use work.wbgenplus_pkg.all;
use work.genram_pkg.all;
use work.wb_read_access_tester_auto_pkg.all;

entity wb_read_access_tester_auto is
Port(
  clk_sys_i   : std_logic;                            -- Clock input for sys domain
  rst_sys_n_i : std_logic;                            -- Reset input (active low) for sys domain
  error_i     : in  std_logic_vector(1-1 downto 0);   -- Error control
  fuse_i      : in  std_logic_vector(32-1 downto 0);  -- Fuse to indicate if the register below has been read.
  fuse_V_i    : in  std_logic_vector(1-1 downto 0);   -- Valid flag - fuse
  stall_i     : in  std_logic_vector(1-1 downto 0);   -- flow control
  
  data_i      : in  t_wishbone_slave_in;
  data_o      : out t_wishbone_slave_out

);
end wb_read_access_tester_auto;

architecture rtl of wb_read_access_tester_auto is

  signal s_pop, s_push    : std_logic;
  signal s_empty, s_full  : std_logic;
  signal r_e_wait, s_e_p  : std_logic;
  signal s_stall          : std_logic;
  signal s_valid,
         s_valid_ok,
         r_valid_check    : std_logic;
  signal r_ack            : std_logic;
  signal r_err            : std_logic;
  signal s_e, r_e, s_w    : std_logic;
  signal s_d              : std_logic_vector(32-1 downto 0);
  signal s_s              : std_logic_vector(4-1 downto 0);
  signal s_a              : std_logic_vector(1-1 downto 0);
  signal s_a_ext,
         r_a_ext0,
         r_a_ext1         : std_logic_vector(3-1 downto 0);
  signal r_error          : std_logic_vector(1-1 downto 0)  := std_logic_vector(to_unsigned(0, 1)); -- Error
  signal s_error_i        : std_logic_vector(1-1 downto 0)  := (others => '0');                     -- Error control
  signal s_stall_i        : std_logic_vector(1-1 downto 0)  := (others => '0');                     -- flow control
  signal r_fuse_V         : std_logic_vector(1-1 downto 0)  := (others => '0');                     -- Valid flag - fuse
  signal s_fuse_V_i       : std_logic_vector(1-1 downto 0)  := (others => '0');                     -- Valid flag - fuse
  signal r_fuse           : std_logic_vector(32-1 downto 0) := (others => '0');                     -- Fuse to indicate if the register below has been read.
  signal s_fuse_i         : std_logic_vector(32-1 downto 0) := (others => '0');                     -- Fuse to indicate if the register below has been read.
  signal r_read_test_reg  : std_logic_vector(32-1 downto 0) := (others => '0');                     -- Read only register that burns a fuse if it was accessed.


begin

  sp : wb_skidpad
  generic map(
    g_adrbits   => 1
  )
  Port map(
    clk_i        => clk_sys_i,
    rst_n_i      => rst_sys_n_i,
    push_i       => s_push,
    pop_i        => s_pop,
    full_o       => s_full,
    empty_o      => s_empty,
    adr_i        => data_i.adr(2 downto 2),
    dat_i        => data_i.dat,
    sel_i        => data_i.sel,
    we_i         => data_i.we,
    adr_o        => s_a,
    dat_o        => s_d,
    sel_o        => s_s,
    we_o         => s_w
  );

  validmux: with to_integer(unsigned(s_a_ext)) select
  s_valid <= 
  s_fuse_V_i(0) when c_fuse_GET,  -- 
  '1'           when others;
  
  s_valid_ok      <=  r_valid_check and s_valid;
  s_e_p           <=  r_e or r_e_wait;
  s_a_ext         <= s_a & "00";
  s_stall         <= s_full;
  s_push          <= data_i.cyc and data_i.stb and not s_stall;
  s_e             <= not (s_empty or s_e_p);
  s_pop           <= s_valid_ok;
  data_o.stall    <= s_stall;
  
  s_error_i   <= error_i;
  s_stall_i   <= stall_i;
  s_fuse_V_i  <= fuse_V_i;
  s_fuse_i    <= fuse_i;
  
  data : process(clk_sys_i)
  begin
    if rising_edge(clk_sys_i) then
      if(rst_sys_n_i = '0') then
        r_e           <= '0';
        r_e_wait      <= '0';
        r_valid_check <= '0';
        r_error         <= std_logic_vector(to_unsigned(0, 1));
        r_fuse          <= (others => '0');
        r_read_test_reg <= (others => '0');
      else
        r_e           <= s_e;
        r_a_ext0      <= s_a_ext;
        r_a_ext1      <= r_a_ext0;
        r_e_wait      <= s_e_p and not s_valid_ok;
        r_valid_check <= (r_valid_check or (s_e_p and not stall_i(0))) and not s_valid_ok;
        r_ack         <= s_pop and not (error_i(0) or r_error(0));
        r_err         <= s_pop and     (error_i(0) or r_error(0));
        data_o.ack    <= r_ack;
        data_o.err    <= r_err;
        
        
        if stall_i = "0" then
          r_error <= (others => '0');
        end if;
        
        if s_fuse_V_i = "1" then r_fuse <= s_fuse_i; end if;  -- 
        
        
        if(s_e = '1') then
          if(s_w = '1') then
            -- WISHBONE WRITE ACTIONS
            case to_integer(unsigned(s_a_ext)) is
              when others => r_error  <= "1";
            end case;
          else
            -- WISHBONE READ ACTIONS
            case to_integer(unsigned(s_a_ext)) is
              when c_fuse_GET           => null;
              when c_read_test_reg_GET  => null;
              when others               => r_error <= "1";
            end case;
          end if; -- s_w
        end if; -- s_e
        
        case to_integer(unsigned(r_a_ext1)) is
          when c_fuse_GET           => data_o.dat <= std_logic_vector(resize(unsigned(r_fuse), data_o.dat'length));           -- 
          when c_read_test_reg_GET  => data_o.dat <= std_logic_vector(resize(unsigned(r_read_test_reg), data_o.dat'length));  -- 
          when others               => data_o.dat <= (others => 'X');
        end case;

        
      end if; -- rst
    end if; -- clk edge
  end process;

end rtl;
